import axios from 'axios';

export const saveMsgsHistory = (
  msgsHistory,
  loggedInUserUserId,
  clickedUserUserId
) => async (dispatch, getState) => {
  try {
    dispatch({
      type: 'SAVE_MSGS_HISTORY_START',
    });
    const { user } = getState();

    const config = {
      headers: {
        Authorization: `Bearer ${user.data.token}`,
      },
    };

    const { data } = await axios.post(
      'http://localhost:9000/api/v1/msgs',
      {
        roomName,
        msgsHistory,
      },
      config
    );

    dispatch({
      type: 'SAVE_MSGS_HISTORY_SUCCESS',
      payload: data,
    });
  } catch (error) {
    dispatch({
      type: 'SAVE_MSGS_HISTORY_FAILURE',
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

export const getMsgHistory = (roomName) => async (dispatch, getState) => {
  try {
    dispatch({
      type: 'GET_MSGS_HISTORY_START',
    });

    const { user } = getState();

    const config = {
      headers: {
        Authorization: `Bearer ${user.data.token}`,
      },
    };
    const { data } = await axios.get(
      `http://localhost:9000/api/v1/msgs/${roomName}`,
      config
    );

    dispatch({
      type: 'GET_MSGS_HISTORY_SUCCESS',
      payload: data,
    });
  } catch (error) {
    dispatch({
      type: 'GET_MSGS_HISTORY_FAILURE',
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};
