import React from 'react';
import IncomingMsg from './IncomingMsg';
import OutgoingMsg from './OutgoingMsg';

function ChatAreaMessages(props) {
  return (
    <div className="chat-area--messages">
      {props.msgs.map((msg) =>
        msg.outgoing === true ? (
          <OutgoingMsg msg={msg} />
        ) : (
          <IncomingMsg msg={msg} />
        )
      )}

    </div>
  );
}

export default ChatAreaMessages;


