const initialState = [];

export const msgsReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'SAVE_MSGS_HISTORY_START':
      return{...state,loading:true}
    case 'GET_MSGS_HISTORY':
      break;
    default:
      return state;
  }
};
