import { Divider } from '@chakra-ui/layout';
import React, { useEffect, useState } from 'react';
import Friends from './Friends';
import { useSelector, useDispatch } from 'react-redux';
import { ClickedUser } from '../redux/actions/userActions';

function FriendsBar() {
  const searchedUser = useSelector((state) => state.searchedUser?.user);
  const [user, setUser] = useState({});
  const [clickedUser, setClickedUser] = useState({});
  const dispatch = useDispatch();

  useEffect(() => {
    if (!searchedUser) {
      setUser(null);
    } else {
      setUser(searchedUser.data);
    }
  }, [searchedUser]);

  useEffect(() => {
    dispatch(ClickedUser(clickedUser));
  }, [clickedUser]);

  const onClick =()=>{
    setClickedUser
  }

  return (
    <div className="friends-bar">
      {user ? (
        <>
          <Friends name={user.name} onClick={onClick} />
          <Divider />
        </>
      ) : (
        <>
          <Friends name="Sourav" lastMessage="BE Careful" />
          <Divider />
          <Friends name="Ekta" lastMessage="How are you?" />
          <Divider />
        </>
      )}
    </div>
  );
}

export default FriendsBar;
