import React from 'react';
import { useSelector } from 'react-redux';

function IncomingMsg({ msgs }) {
  const loggedInUser = useSelector((state) => state.user.data.data.user);

  return (
    <div className="incoming">
      {msgs?.map(({ message, user, current_time }) => {
        return (
          <div key={Math.random()} className="bubble">
            <span className="msg_sender">
              {loggedInUser.userId === user ? 'You' : user}
            </span>
            <div className="msg_part">{message}</div>
            <span className="message-time">{current_time}</span>
          </div>
        );
      })}
    </div>
  );
}

export default IncomingMsg;
