import React from 'react';
import { Avatar } from '@chakra-ui/avatar';
import { AiOutlineLogout } from 'react-icons/ai';
import {
  Popover,
  PopoverContent,
  PopoverHeader,
  PopoverBody,
  PopoverFooter,
  PopoverArrow,
  PopoverCloseButton,
  Button,
  ButtonGroup,
} from '@chakra-ui/react';

function ChatProfileBar({ user }) {
  const [isOpen, setIsOpen] = React.useState(false);

  return (
    <div className="profile-bar--chat">
      <div className="profile-barLeft--chat">
        <div className="profile-barAvatar--chat">
          <Avatar name={user?.name} />
        </div>
        <div className="profile-bar--text">
          <span>{user?.name}</span>
        </div>
      </div>
      <div className="profile-barMenu">
        <Button
          rightIcon={<AiOutlineLogout />}
          colorScheme="teal"
          variant="ghost"
        >
          Logout
        </Button>
      </div>
    </div>
  );
}

export default ChatProfileBar;
