import { Avatar } from '@chakra-ui/avatar';
import React from 'react';
import { useSelector } from 'react-redux';
import { AiOutlineLogout } from 'react-icons/ai';
import {
  Popover,
  PopoverTrigger,
  PopoverContent,
  PopoverHeader,
  PopoverBody,
  PopoverFooter,
  PopoverArrow,
  PopoverCloseButton,
  Button,
  ButtonGroup,
} from '@chakra-ui/react';

function ProfileBar() {
  const user = useSelector((state) => state.user?.data?.data.user);
  const [isOpen, setIsOpen] = React.useState(false);
  const open = () => setIsOpen(!isOpen);
  const close = () => setIsOpen(false);
  return (
    <div className="profile-bar">
      <div className="profile-barAvatar">
        <Avatar name={user.name} />
      </div>
      <div className="profile-bar--text">
        <span>{user.name}</span>
      </div>
      <div className="profile-barMenu">
        <Button mr={5} onClick={open}>
          <AiOutlineLogout className="logout-button" />
        </Button>
        <Popover
          returnFocusOnClose={false}
          isOpen={isOpen}
          onClose={close}
          placement="right"
          closeOnBlur={false}
        >
          <PopoverContent>
            <PopoverHeader fontWeight="semibold">Confirmation</PopoverHeader>
            <PopoverArrow />
            <PopoverCloseButton />
            <PopoverBody>Are you sure you want to logout?</PopoverBody>
            <PopoverFooter d="flex" justifyContent="flex-end">
              <ButtonGroup size="sm">
                <Button colorScheme="red">Logout</Button>
              </ButtonGroup>
            </PopoverFooter>
          </PopoverContent>
        </Popover>
      </div>
    </div>
  );
}

export default ProfileBar;
