import React, { useState, useEffect } from 'react';

import ChatProfileBar from './ChatProfileBar';
import ChatAreaMessages from './ChatAreaMessages';
import ChatAreaInput from './ChatAreaInput';
import { io } from 'socket.io-client';
import { useSelector } from 'react-redux';
import moment from 'moment';
const socket = io('http://localhost:9000/');

function ChatArea() {
  const [msg, setMsg] = useState('');
  const [disableButton, setDisableButton] = useState(false);
  const [msgsHistory, setMsgsHistory] = useState([]);
  const [incomingMsgsHistory, setIncomingMsgsHistory] = useState([]);
  const [outgoingMsgsHistory, setOutgoingMsgsHistory] = useState([]);

  const clickedUser = useSelector((state) => state.clickedUser.user);
  const loggedInUser = useSelector((state) => state.user.data.data.user);

  const onChange = (e) => {
    if (e.target.value.length === 0) {
      setDisableButton(true);
    } else {
      setDisableButton(false);

      setMsg(e.target.value);
    }
  };
  useEffect(() => {
    const clickedUserUserId = clickedUser?.userId;
    if (typeof clickedUserUserId === 'undefined') return;
    else {
      const loggedInUserUserId = loggedInUser.userId;

      let roomName =
        parseInt(loggedInUserUserId, 24) * parseInt(clickedUserUserId, 24);
      socket.emit('roomNameToJoin', roomName);
    }
  }, [clickedUser]);

  const onSend = (e) => {
    socket.emit('message', { message: msg, user: loggedInUser.userId });

    let current_time = moment().format('HH:mm');

    setMsgsHistory([
      ...msgsHistory,

      { message: msg, user: loggedInUser.userId, current_time, outgoing:true },
    ]);
    setMsg('');
  };

  socket.on('messageToUser', ({ message, user, current_time }) => {
    setMsgsHistory([
      ...msgsHistory,
      { message, user, current_time,outgoing:false },
    ]);
    //console.log(incomingMsgsHistory);
    //setMsgsHistory([...msgsHistory, message]);
  });
console
  return (
    <div className="chat-area">
      <ChatProfileBar user={clickedUser} />
      <ChatAreaMessages
        /*msgs={msgsHistory}*/ incomingMsgsHistory={incomingMsgsHistory}
        outgoingMsgsHistory={outgoingMsgsHistory}
      />
      <ChatAreaInput
        onSend={onSend}
        msg={msg}
        onChange={onChange}
        disable={disableButton}
      />
    </div>
  );
}

export default ChatArea;
