import React from 'react';
import { Avatar } from '@chakra-ui/avatar';
import { AiOutlineLogout } from 'react-icons/ai';
import {
  Popover,
  PopoverContent,
  PopoverHeader,
  PopoverBody,
  PopoverFooter,
  PopoverArrow,
  PopoverCloseButton,
  Button,
  ButtonGroup,
} from '@chakra-ui/react';

function ChatProfileBar({ user }) {
  const [isOpen, setIsOpen] = React.useState(false);
  const open = () => setIsOpen(!isOpen);
  const close = () => setIsOpen(false);
  return (
    <div className="profile-bar--chat">
      <div className="profile-barLeft--chat">
        <div className="profile-barAvatar--chat">
          <Avatar name={user?.name} />
        </div>
        <div className="profile-bar--text">
          <span>{user?.name}</span>
        </div>
      </div>
      <div className="profile-barMenu">
        <Button mr={5} onClick={open}>
          <AiOutlineLogout className="logout-button" />
        </Button>
        <Popover
          returnFocusOnClose={false}
          isOpen={isOpen}
          onClose={close}
          place
          closeOnBlur={false}
        >
          <PopoverContent>
            <PopoverHeader fontWeight="semibold">Confirmation</PopoverHeader>
            <PopoverArrow />
            <PopoverCloseButton />
            <PopoverBody>Are you sure you want to logout?</PopoverBody>
            <PopoverFooter d="flex" justifyContent="center">
              <ButtonGroup size="sm">
                <Button colorScheme="red">Logout</Button>
              </ButtonGroup>
            </PopoverFooter>
          </PopoverContent>
        </Popover>
      </div>
    </div>
  );
}

export default ChatProfileBar;
