import axios from 'axios';

export const getFriendsLastChattedWith = () => async (dispatch, getState) => {
  try {
    dispatch({
      type: 'GET_FRIENDS_CHATTED_WITH_START',
    });

    const { user } = getState();

    const config = {
      headers: {
        Authorization: `Bearer ${user.data.token}`,
      },
    };

    const { data } = await axios.get(
      'http://localhost:9000/api/v1/msgs/',
      config
    );

    dispatch({
      type: 'GET_FRIENDS_CHATTED_WITH_SUCCESS',
      payload: data,
    });
  } catch (error) {
    dispatch({
      type: 'GET_FRIENDS_CHATTED_WITH_FAILURE',
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};
