import { Avatar } from '@chakra-ui/avatar';
import React from 'react';
import { useSelector } from 'react-redux';
import { AiOutlineLogout } from 'react-icons/ai';

function ProfileBar() {
  const user = useSelector((state) => state.user?.data?.data.user);

  return (
    <div className="profile-bar">
      <div className="profile-barAvatar">
        <Avatar name={user.name} />
      </div>
      <div className="profile-bar--text">
        <span>{user.name}</span>
      </div>
      <div className="profile-barMenu">
        <Button mr={5} onClick={open}>
          <AiOutlineLogout className="logout-button" />
        </Button>
        <Popover
          returnFocusOnClose={false}
          isOpen={isOpen}
          onClose={close}
          placement="right"
          closeOnBlur={false}
        >
          <PopoverTrigger>
            <Button colorScheme="pink">Popover Target</Button>
          </PopoverTrigger>
          <PopoverContent>
            <PopoverHeader fontWeight="semibold">Confirmation</PopoverHeader>
            <PopoverArrow />
            <PopoverCloseButton />
            <PopoverBody>
              Are you sure you want to continue with your action?
            </PopoverBody>
            <PopoverFooter d="flex" justifyContent="flex-end">
              <ButtonGroup size="sm">
                <Button variant="outline">Cancel</Button>
                <Button colorScheme="red">Apply</Button>
              </ButtonGroup>
            </PopoverFooter>
          </PopoverContent>
        </Popover>
      </div>
    </div>
  );
}

export default ProfileBar;
