import React from 'react';
import { useSelector } from 'react-redux';

function OutgoingMsg({ message, user, current_time }) {
  const loggedInUser = useSelector((state) => state.user.data.data.user);

  return (
    <>
      loggedInUser.userId === user ? null : (
      <div className="outgoing" key={Math.random()}>
        <div key={Math.random()} className="bubble">
          <span className="msg_sender">
            {loggedInUser.userId === user ? 'You' : logg.name}
          </span>
          <div className="msg_part">{message}</div>
          <span className="message-time">{current_time}</span>
        </div>
      </div>
      )
    </>
  );
}

export default OutgoingMsg;
